</script>

<style lang="scss" scoped>
  .app-menu_wrapper {
    width: 100%;
    border-top: 1px solid $border-color;
    .app-menu_container {

      .menu-list {
        display: flex;
        align-items: center;
        justify-content: space-evenly;
        padding: 7px;
        margin-bottom: 10px;
        /*border-radius: 20px;*/
        //background-color: $gainboro;

        .menu-item {
          width: 100%;
          height: 100%;
          padding: 7px;
          /*border-radius: 20px;*/
          cursor: pointer;
          text-align: center;
          &:hover, &.active {

            .item-icon {
              svg {
                &:first-child {
                  display: none;
                }
                &:last-child {
                  display: block;
                }
                fill: $primary;
                transition: 1s;
              }
            }
          }
          &.active {
            //border-bottom: 1px solid $primary;
          }

          .item-text {
            display: none;
            @include font-text($font-14, normal, null);
          }

          .item-icon {
            width: 100%;
            svg {
              margin: 0 auto;
              fill: $color;

              &:last-child {
                display: none;
              }
            }
          }

        }

      }

    }

  }
</style>
