<script>
    export default {
        methods: {
            changeMode(e) {
                if (!e.target.checked)
                    this.$colorMode.preference = 'dark'
                else
                    this.$colorMode.preference = 'light'
            }
        }
    }
</script>

<style lang="scss" scoped>

  label {
    position: relative;
    display: inline-block;
    width: 100px;
    height: 30px;
    margin-left: auto;
    background-color: rgba($gainboro, .5);
    border-radius: 20px;
    cursor: pointer;

    input[type="checkbox"] {
      display: none;

      &:checked + .toggle {
        transform: translateX(100%);
        background-color: $bg-secondary;
      }
      &:checked ~ .names .dark {
        opacity: 1;
      }
      &:checked ~ .names .light {
        opacity: .5;
        color: $dark;
      }
    }

    .toggle {
      position: absolute;
      width: 50%;
      height: 30px;
      border-radius: 20px;
      background-color: $bg-secondary;
      box-shadow: 0 2px 15px rgba(0,0,0,.15);
      transition: transform .3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    }

    .names {
      position: absolute;
      width: 100%;
      height: 30px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      text-align: center;
      user-select: none;
      .dark, .light {
        padding: 0 13px;
      }

      .dark {
        opacity: .5;
        color: $dark;
      }
    }
  }
</style>
